import React from 'react';
import './testimonials.css';
import Image1 from '../../assets/avatar-1.svg';
import Image3 from '../../assets/avatar-3.svg';
import { Pagination } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';


const data = [
  {
    id: 1,
    image: Image1,
    title: "John Doe",
    subtitle: "Product designer at Dribble",
    comment:
      "I enjoy working with the theme and learn so much. You       guys make the process fun and interesting. Good luck! ðŸ‘",
  },
  {
    id: 2,
    image: Image3,
    title: "John Doe",
    subtitle: "Product designer at Dribble",
    comment:
      "I enjoy working with the theme and learn so much. You       guys make the process fun and interesting. Good luck! ðŸ‘",
  },
];

const Testimonials = () => {
  return (
    <section className='testimonials container section'>
      <h2 className='section_title'>Testimonials</h2>

      <Swiper className='testimonials_container grid'>
        {data.map(({id, image, title, subtitle, comment}) => {
          return (
            <div className='testimonial_item' key={id}>
              <div className='thumb'>
                <img src={image} alt='' />
              </div>
              <h3 className='testimonials_title'>{title}</h3>
              <span className='subtitle'>{subtitle}</span>
              <div className='comment'>{comment}</div>
            </div>
          )
        })}
      </Swiper>
    </section>
  )
}

export default Testimonials
